#waybar {
    background: transparent;
    padding: 8px;
}

window#waybar {
    background-color: transparent;
}

.modules-left,
.modules-center,
.modules-right {
    margin: 4px 4px 0px 4px;
}

/* Workspace Buttons - Orange Slider Update */
#workspaces button {
    color: @color6;
    background-color: alpha(@background, 0.75);
    border: 1px solid @color4;
    border-radius: 16px;
    padding: 4px 12px;
    margin: 2px 2px;
    /* Smooth transition for the 'sliding' width effect */
    transition: all 0.4s cubic-bezier(.55,-0.68,.48,1.68);
}

#workspaces button:hover {
    background-color: alpha(@color4, 0.2);
    color: @color4;
}

#workspaces button.active {
    color: @background;
    background: @color3; /* Vibrant Orange Slider */
    border: 1px solid @color4;
    min-width: 30px; /* Creates the expanded pill/slider look */
    font-weight: bold;
}

#workspaces button.urgent {
    background-color: @color1;
    color: @color15;
}

